import React, {useEffect, useRef} from "react";
import * as d3 from 'd3';
import  './HeatMap.css';

function groupBy(rawData, key) {
    return rawData.reduce(function(previousVal, curretnVal) {
        (previousVal[curretnVal[key]] = previousVal[curretnVal[key]] || []).push(curretnVal);
        return previousVal;
    }, {});
};

const HeatMap = ()  => {
    const d3HeatMap = useRef()

    const parseDate = d3.timeParse("%Y-%m-%d")

    useEffect(()=>{
        fetch("StreamingHistoryDesi.json")
            .then(response => response.json())
            .then(data => {
                data.forEach(function(entry, idx, data){
                    data[idx]["date"] = data[idx].endTime.split(" ")[0];
                    delete data[idx].artistName;
                    delete data[idx].trackName;
                    delete data[idx].endTime;
                });

                let filteredData = Object.values(groupBy(data, "date"));

                const streamingData = filteredData.map(function(day){
                    let date = day[0]["date"];
                    day = day.reduce(function(prev, cur){
                        prev["totalTracks"] = prev["totalTracks"] + 1;
                        prev["totalPlayed"] = prev["totalPlayed"] + cur["msPlayed"];
                        return prev;
                    }, {"totalTracks": 0, "totalPlayed": 0});
                    day["date"] = parseDate(date);
                    return day;
                });

                console.log(streamingData);

                const margin = {top: 20, right: 30, bottom: 30, left: 30}
                const width = parseInt(d3.select('#d3demo').style('width')) - margin.left - margin.right
                const height = parseInt(d3.select('#d3demo').style('height')) - margin.top - margin.bottom

                const svg = d3.select(d3HeatMap.current)
                    .attr('width', width + margin.left + margin.right)
                    .attr('height', height + margin.top + margin.bottom)
                    .style("background-color", "red")
                    .append('g')
                    .attr('transform', 'translate('+ margin.left + ',' + margin.top + ')');

                const x = d3.scaleTime()
                    .domain(d3.extent(streamingData, function(d){return d.date}))
                    .range([0, width])

                svg.append('g')
                    .attr('transform', 'translate(0,' + height + ')')
                    .call(d3.axisBottom(x))

                // Get the max value of counts
                const max = d3.max(streamingData, function(d){return d.totalPlayed / 1000})

                // y axis scale
                const y = d3.scaleLinear()
                    .domain([0, max])
                    .range([height,0])

                svg.append('g')
                    .call(d3.axisLeft(y))


                // Draw line
                svg.append('path')
                    .datum(streamingData)
                    .attr('fill', 'none')
                    .attr('stroke','white')
                    .attr('stroke-width', 3)
                    .attr('d', d3.line()
                        .x(function(d){return x(d.date)})
                        .y(function(d){return y(parseInt(d.totalPlayed / 1000))})
                    )
                //
                // // Add title
                // svg.append('text')
                //     .attr('x',(width/2))
                //     .attr('y', (margin.top/5 - 10))
                //     .attr('text-anchor', 'middle')
                //     .attr('font-size', '16px')
                //     .attr('fill','white')
                //     .text('New York City Film Permits entered in 2020 - Shooting Permit')
            })
    })

    return (
        <div id='d3demo'>
            <svg ref={d3HeatMap}></svg>
        </div>
    )
}

export default HeatMap;
